//jQuery.noConflict();
// 'armadillo' is the global object for admin functions
var ArmadilloAdmin = {
	auto_run : (function(){
		$ = jQuery;
		jQuery(function(){
			ArmadilloAdmin.removeNotifications();
			ArmadilloAdmin.adjustFooter();
			ArmadilloAdmin.disableColumnSorting();
		});
		jQuery(document).ready(function(){
			ArmadilloAdmin.blogSelect();
		});
	})(),
	
	removeNotifications: function() {
		setTimeout(function() {
			if ( $('p.notification').is(':visible') ) {
				$('p.notification').fadeTo('normal', 0).slideUp(500);
			}
		}, 3000);
	},
	
	adjustFooter: function() {
		var windowHeight = $(window).height();
		var pageHeight = $('#adminPanelContainer').height();
		if ( pageHeight > windowHeight ) { $('#bottomPanel').animate({bottom: '-70'}, 1000, function(){ $(this).css({position: 'relative', bottom: 'auto'}); }); }
		else { $('#adminPanelContainer').css('min-height', windowHeight); }
	},

	disableColumnSorting: function() {
		$('#summaryList .disabled.sorting').unbind('click');
	},
	
	armadilloSetup: function(dbMessage1, dbMessage2, psswrdMessage1, psswrdMessage2, emailMessage) {
		/* v1.7+ online setup */
		$('#adminPassword').passField({allowEmpty: false});
		$('#adminConfirmPassword').passField({showToggle: true, showGenerate: false});
		/* Original setup process completely entirely online */
		$('#dbDetailsButton').click(function(){ $('#databaseDetails, #databaseDetails+#goToStep2').slideDown(200, function(){ $('#databaseDetails, #databaseDetails+#goToStep2').fadeTo(300, 1); }); });
		$('#goToStep2').click(function(){
			// DB Connect Settings
			var hostname = "dbHostname=" + $('#dbHostname').val();
			var dbname = "dbName=" + $('#dbName').val();
			var dbuser = "dbUser=" + $('#dbUser').val();
			var dbpassword = "dbPassword=" + $('#dbPassword').val();
			var dbSettings = hostname + "&" + dbname + "&" + dbuser + "&" + dbpassword;
			
			$.ajax({
				type: 'get',
				cache: false,
				dataType: 'text',
				url: './../../core/model/updateArmadilloSettings.php?action=checkDBsettings',
				data: dbSettings,
				success: function(result) {
					if (result == "true") { $('#setupStep1').fadeTo( 300, 0 ).slideUp( 1, function(){
							$('#setupStep2').slideDown( 1 ).fadeTo( 500, 1 ); 
							$('#adminPassword').passField({allowEmpty: false});
							$('#adminConfirmPassword').passField({showToggle: true, showGenerate: false});
						});
					} else { var notification = dbMessage1;
						if ( $('.notification').length == 0 ) { 
							$('<p class="notification bg-info text-center" style="display: none; opacity: 0;">' + notification + '</p>').prependTo('#adminPanelContainer').slideDown(500).fadeTo(500, 1).delay(5000).fadeTo(500,0).slideUp(500); } 
						else { $('.notification').empty().html(notification).slideDown(500).fadeTo(500, 1).delay(5000).fadeTo(500,0).slideUp(500); } } },
				error:  function() { $('.notification').empty().append(dbMessage2); }
			});
		});
		$('#goToStep3').click(function(){
			// Compare passwords entered
			if ( $('#adminPassword').val() == $('#adminConfirmPassword').val() && $('#adminPassword').val() != '' && $('#adminEmail').val() != '' ) { $('#setupStep2').fadeTo( 300, 0 ).slideUp( 1, function(){ $('#setupStep3').slideDown( 1 ).fadeTo( 500, 1 ); }); }
			else { var notification = $('#adminPassword').val() == '' ? psswrdMessage1 : psswrdMessage2;
				var emailNotification = $('#adminEmail').val() == '' ? ' ' + emailMessage : '';
				if ( $('.notification').length == 0 ) { 
					$('<p class="notification" style="display: none; opacity: 0;">' + notification + emailNotification + '</p>').prependTo('#adminPanelContainer').slideDown(500).fadeTo(500, 1).delay(5000).fadeTo(500,0).slideUp(500); } 
				else { $('.notification').empty().html(notification + emailNotification).slideDown(500).fadeTo(500, 1).delay(5000).fadeTo(500,0).slideUp(500); } }
		});
	},

	blogSelect: function() {
		$('#blogSelect').change(function(){
			var selected = $(this).find("option:selected").val();
			if ( $(this).parents('.contentAdminPanel').length > 0 ) {
				//refresh page and pass in selected blog value
				window.location = window.location + '../../' + selected + '/posts/';
			} else if ( $(this).parents('.settingsAdminPanel').length > 0 ) {
				//hide and show relevant options
				$('.blogSettings').hide();
				$('#'+selected+'-Settings').fadeIn();
				
			}
		});
	},
	
	addTagsCategories: function() {
		if ( !$('#categoriesList').is(':empty') ) { $('#categoriesList').slideDown().fadeTo(500, 1); $('.categoryItem').slideDown().fadeTo(500, 1); }
		if ( !$('#tagsList').is(':empty') ) { $('#tagsList').slideDown().fadeTo(500, 1); $('.tagItem').slideDown().fadeTo(500, 1); }
		$('#addCategories').click(function(){
			var categories = $('#categories').val().split(',');
			if ( categories[0] != '' ) {
				$('#categories').val('');
				for ( var i = 0; i < categories.length; i++ ) {	$('#categoriesList').prepend('<span class="categoryItem"><input type="checkbox" name="categoryList[]" value="' + categories[i] + '" checked="checked" />&nbsp;' + $.trim(categories[i]) + '</span><br/>'); }
			if ( !$('#categoriesList').is(':visible') ) { $('#categoriesList').slideDown().fadeTo(500, 1); }
			$('.categoryItem').slideDown().fadeTo(500, 1);
			}
			
		});
		$('#addTags').click(function(){
			var tags = $('#tags').val().split(',');
			if ( tags[0] != '' ) {
				$('#tags').val('');
				for ( var i = 0; i < tags.length; i++ ) { $('#tagsList').prepend('<span class="tagItem"><input type="checkbox" name="tagList[]" value="' + tags[i] + '" checked="checked" />&nbsp;' + $.trim(tags[i]) + "</span><br/>"); }
			if ( !$('#tagsList').is(':visible') ) { $('#tagsList').slideDown().fadeTo(500, 1); }
			$('.tagItem').slideDown().fadeTo(500, 1);
			}
		});
	},
	
	makeMenuSortable: function() {
		$('#armadilloContentMenu ol').nestedSortable({
			disableNesting: 'no-nest',
			forcePlaceholderSize: true,
			handle: 'div',
			helper:	'clone',
			items: 'li',
			maxLevels: 3,
			opacity: .6,
			placeholder: 'placeholder',
			revert: 250,
			tabSize: 25,
			tolerance: 'pointer',
			toleranceElement: '> div'
		});
	},
	
	saveAllSettings: function(settingsMessage1, settingsMessage2, settingsError1, settingsError2, settingsError3, settingsError4) {
		$.fn.serializeObject = function()
		{
		    var o = {};
		    var a = this.serializeArray();
		    $.each(a, function() {
		        if (o[this.name] !== undefined) {
		            if (!o[this.name].push) {
		                o[this.name] = [o[this.name]];
		            }
		            o[this.name].push(this.value || '');
		        } else {
		            o[this.name] = this.value || '';
		        }
		    });
		    return o;
		};
		$('.saveAllSettings').on('click', function(){
			
			var rssErrors = 0;

			$('input.enableRSS').each(function(){
				if ( $(this).val() == 'TRUE' && $(this).parents('.blogRSS').find('input.blogRSSfileName').val() == '' && (typeof $(this).parents('.blogRSS').find('input.blogRSSfileName').parents('fieldset').attr('disabled') == 'undefined') ) {
					++rssErrors;
				}
			});

			if ( rssErrors > 0 ) {
				$('#saveResults').addClass('bg-info').removeClass('bg-success bg-warning').append(settingsError4).slideDown().delay(5000).slideUp(500, function() { $('#saveResults').empty(); });
			} else {
				// Grab all settings and combine them into a single object
				var armSettings = $('form').serializeObject();
				// Send nav order settings separately, appears they can't be combined with other settings and have them all saved correctly
				var navOrder = $('#armadilloContentMenu ol').nestedSortable('serialize');

				//Send All Settings and Options to PHP Script to be saved in the database
				$.ajax({
					type: 'post',
					cache: false,
					url: './../../core/model/updateArmadilloSettings.php?action=updateAllOptions',
					dataType: 'json',
					data: armSettings,
					success: function(result,data) {
								if ( result ) { $('#saveResults').addClass('bg-success').removeClass('bg-warning bg-info').append(settingsMessage1); }
								else if ( result == "rssNotUpdated" ) { $('#saveResults').addClass('bg-warning').removeClass('bg-info bg-success').append(settingsMessage2); }
								else { $('#saveResults').addClass('bg-warning').removeClass('bg-info').append(settingsError1); }
								// Save nav order
								$.ajax({
									type: 'post',
									cache: false,
									url: './../../core/model/updateArmadilloSettings.php?action=updateNavOrder',
									dataType: 'json',
									data: navOrder,
									success: function(result,data) { if ( !result ) { $('#saveResults').addClass('bg-warning').removeClass('bg-info bg-success').append(settingsError3); } },
									error:  function(result) { console.log(result); $('#saveResults').addClass('bg-warning').removeClass('bg-info bg-success').append(settingsError3); }
								});
							},
					error:  function(result) { console.log(result); $('#saveResults').addClass('bg-warning').removeClass('bg-info bg-success').append(settingsError2); },
					complete: function() {
						$('#saveResults').slideDown().delay(5000).slideUp(500, function() { $('#saveResults').addClass('bg-info').removeClass('bg-warning bg-success').empty(); });
					}
				});
			}
		});
	},

	settingsNotification: function() {
		$(document).ajaxStart(function(){
			$('.updateProgress').fadeTo(300,1);
		}).ajaxStop(function(){
			$('.updateProgress').fadeTo(400,0);
		});
	},
	
	displayRelevantSettings: function() {
		var menuDisplayOptions = $('input[name=menuDisplayOptions]:checked').val();
		if ( ( menuDisplayOptions == 'moveToBefore' || menuDisplayOptions == 'moveToAfter' ) && !$('#siteNavContainerEntry').is(':visible') ) { $('#siteNavContainerEntry').slideDown().fadeTo(500,1); }

		function toggleSettings(elementToClick,elementToToggle,firstValueToCompare,secondValueToCompare) {
			var clickElement = $(elementToClick);
			var toggleElement = $(elementToToggle);
			clickElement.click(function(){
				if ( secondValueToCompare != '' ) {
					if ( $(this).val() == firstValueToCompare || $(this).val() == secondValueToCompare ) {
						if ( !toggleElement.is(':visible') ) { toggleElement.slideDown().fadeTo(500,1); }
					} else {
						if ( toggleElement.is(':visible') ) { toggleElement.fadeTo(500,0).slideUp(); } 
					}
				} else {
					if ( $(this).val() == firstValueToCompare ) {
						if ( !toggleElement.is(':visible') ) { toggleElement.slideDown().fadeTo(500,1); }
					} else {
						if ( toggleElement.is(':visible') ) { toggleElement.fadeTo(500,0).slideUp(); } 
					}
				}
			});
		}

		toggleSettings('input[name=menuDisplayOptions]','#siteNavContainerEntry','moveToBefore','moveToAfter');
		//toggleSettings('input[name=enableBlogRSS]','#blogUrlSettings','TRUE','');
		// toggleSettings('input[name=displayBlogCategories]','#categoriesTitleEntry','TRUE','');
		// toggleSettings('input[name=displayBlogCategories]','.blogCategories .settingDetails','TRUE','');
		// toggleSettings('input[name=displayBlogTags]','#tagsTitleEntry','TRUE','');
		// toggleSettings('input[name=displayBlogTags]','.blogTags .settingDetails','TRUE','');
		// toggleSettings('input[name=displayBlogComments]','#disqusShortnameEntry','TRUE','');
		// toggleSettings('input[name=displayBlogArchiveLinks]','#blogArchiveLinkSettings','TRUE','');
		// toggleSettings('input[name=displayPostAuthor]','#postAuthorSettings','TRUE','');
		toggleSettings('input[name=displaySocialSharingLinks]','#armadilloSocialSharingCode','TRUE','');
		toggleSettings('input[name=displaySocialSharingLinks]','.socialSharing p.note.help-block','TRUE','');
		//if ( $('input[name=enableBlogRSS]:checked').val() == 'TRUE' && !$('#blogUrlSettings').is(':visible') ) { $('#blogUrlSettings').slideDown().fadeTo(500,1); }
		// if ( $('input[name=displayBlogCategories]:checked').val() == 'TRUE' && !$('#categoriesTitleEntry').is(':visible') ) { $('#categoriesTitleEntry, .blogCategories .settingDetails').slideDown().fadeTo(500,1); }
		// if ( $('input[name=displayBlogTags]:checked').val() == 'TRUE' && !$('#tagsTitleEntry').is(':visible') ) { $('#tagsTitleEntry, .blogTags .settingDetails').slideDown().fadeTo(500,1); }
		// if ( $('input[name=displayBlogComments]:checked').val() == 'TRUE' && !$('#disqusShortnameEntry').is(':visible') ) { $('#disqusShortnameEntry').slideDown().fadeTo(500,1); }
		// if ( $('input[name=displayBlogArchiveLinks]:checked').val() == 'TRUE' && !$('#blogArchiveLinkSettings').is(':visible') ) { $('#blogArchiveLinkSettings').slideDown().fadeTo(500,1); }
		// if ( $('input[name=displayPostAuthor]:checked').val() == 'TRUE' && !$('#postAuthorSettings').is(':visible') ) { $('#postAuthorSettings').slideDown().fadeTo(500,1); }
		// if ( $('input[name=displaySocialSharingLinks]:checked').val() == 'TRUE' && !$('#armadilloSocialSharingCode').is(':visible') ) { $('#armadilloSocialSharingCode, .socialSharing p.note.help-block').slideDown().fadeTo(500,1); }
		// if ( $('input[name=enableBlogRSS]:checked').val() == 'FALSE' ) { $('.blogRSS fieldset').attr('disabled',true); }
		$('input.enableRSS').each(function(){
			if ( $(this).val() == 'FALSE' && $(this).attr('checked') == 'checked' ) { $(this).parents('.blogRSS').find('fieldset').attr('disabled',true); };
		});
		$('input.enableRSS').click(function(){
			if ( $(this).val() == 'TRUE' ) { $(this).parents('.blogRSS').find('fieldset').attr('disabled',false); }
			else { $(this).parents('.blogRSS').find('fieldset').attr('disabled',true); }
		});
		$('#settingsToggle label').click(function(){
			if ( $(this).find('input').val() == 'general' ) { $('#generalSettings').show(); $('#blogSettings').hide(); }
			else if ( $(this).find('input').val() == 'blog' ) { $('#generalSettings').hide(); $('#blogSettings').show(); }
		});
	},
	
	toggleContentDataEntryFields: function() {
		$('.toggle').on ('click', function(){
			
			if ( $(this).hasClass('pageSidebar') ) {
				if ( $('#contentEditorContainer').length == 0 ) {
					if ( !$('#sidebarEditorContainer').is(':visible') ) {
						$('.sidebarNotification').slideDown( 300 ).fadeTo( 500, 1, function(){ $('#sidebarEditorContainer').slideDown( 300 ).fadeTo( 500, 1 ); });
						$(this).fadeTo(200, 0, function() { $(this).html('Edit Main Content').fadeTo(300, 1); });
					} else {
						$('#sidebarEditorContainer, .sidebarNotification').fadeTo( 300, 0 ).slideUp( 300 );
						$(this).fadeTo(200, 0, function() { $(this).html('Edit Sidebar').fadeTo(300, 1); });
					}
				} else {
					$('#contentFormHeight').animate({'min-height': 510}, 500);
					if ( $('#contentEditorContainer').is(':visible') ) {
						$('#contentEditorContainer').fadeTo( 300, 0 ).slideUp( 1, function(){ 
							$('.sidebarNotification').slideDown( 1 ).fadeTo( 300, 1, function(){ $('#sidebarEditorContainer').slideDown( 1 ).fadeTo( 500, 1 ); }); });
						$(this).fadeTo(200, 0, function() { $(this).html('Edit Main Content').fadeTo(300, 1); });
					} else { 
						$('#sidebarEditorContainer, .sidebarNotification').fadeTo( 300, 0 ).slideUp( 1, function(){ 
							$('#contentEditorContainer').slideDown( 1 ).fadeTo( 300, 1 ); $('#contentFormHeight').animate({'min-height': 460}, 500); });
						$(this).fadeTo(200, 0, function() { $(this).html('Edit Sidebar').fadeTo(300, 1); });
					}
				}
			}
			if ( $(this).hasClass('metaContent') ) {
				$(this).toggleClass('active');
				$('#metaContentContainer').slideToggle();
			}
			if ( $(this).hasClass('postSummary') ) {
				$(this).toggleClass('active');
				$('#summaryContentContainer').slideToggle();
			}
			if ( $(this).hasClass('internalLinks') ) {
				$(this).toggleClass('active');
				$('#internalLinksContainer').slideToggle();
			}
		});
	}
};

